CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `d_hr`.`records_of_managers_per_employee` AS select `e`.`employee_id` AS `employee_id`,concat(`e`.`first_name`,' ',`e`.`last_name`) AS `employee`,`e`.`job_title` AS `job_title`,`e`.`salary` AS `salary`,`m`.`first_name` AS `manager` from (`d_hr`.`employees` `e` left join `d_hr`.`employees` `m` on((`e`.`reports_to` = `m`.`employee_id`))) order by `m`.`first_name`,`e`.`salary` desc